(18, 'Wordpress', 'Function.php', '<p><!--?php</p--></p>\r\n\r\n<blockquote>\r\n<p>&lt;?php</p>\r\n\r\n<p>/*******************************************</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Adding CSS and JS Files</p>\r\n\r\n<p>*******************************************/</p>\r\n\r\n<p>function kaho_style_enqueue() {</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;wp_enqueue_style(&#39;bootstrapcss&#39;,get_template_directory_uri().&#39;/css/bootstrap.css&#39;);</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;wp_enqueue_style(&#39;fontawesomecss&#39;,get_template_directory_uri().&#39;/css/font-awesome.css&#39;);</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;wp_enqueue_style(&#39;slickcss&#39;,get_template_directory_uri().&#39;/css/slick.css&#39;);</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;wp_enqueue_style(&#39;extracss&#39;,get_template_directory_uri().&#39;/css/extra.css&#39;);</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;wp_enqueue_style(&#39;stylecss&#39;,get_template_directory_uri().&#39;/css/style.css&#39;);</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;wp_enqueue_style( &#39;load-fa-1&#39;, &#39;//fonts.googleapis.com/css?family=EB+Garamond:400,400i,600,600i&#39; );</p>\r\n\r\n<p>}</p>\r\n\r\n<p>add_action( &#39;wp_enqueue_scripts&#39;, &#39;kaho_style_enqueue&#39; );</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>function kaho_script_enqueue() {</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;wp_enqueue_script(&#39;jqueryfile&#39;, get_template_directory_uri().&#39;/js/jquery.js&#39;, array(&#39;jquery&#39;), &#39;&#39;,true);</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;wp_enqueue_script(&#39;jqmigratejs&#39;, get_template_directory_uri().&#39;/js/jquery.migrate.js&#39;, array(&#39;jquery&#39;), &#39;&#39;,true);</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;wp_enqueue_script(&#39;slickjs&#39;, get_template_directory_uri().&#39;/js/slick.js&#39;, array(&#39;jquery&#39;), &#39;&#39;,true);</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;wp_enqueue_script(&#39;fitvidsjs&#39;, get_template_directory_uri().&#39;/js/fitvids.js&#39;, array(&#39;jquery&#39;), &#39;&#39;,true);</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;wp_enqueue_script(&#39;mainjs&#39;, get_template_directory_uri().&#39;/js/main.js&#39;, array(&#39;jquery&#39;), &#39;&#39;,true);</p>\r\n\r\n<p>}</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;add_action( &#39;wp_enqueue_scripts&#39;, &#39;kaho_script_enqueue&#39; );&nbsp;&nbsp;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p>/*******************************************</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Adding Menus to Admin Panel</p>\r\n\r\n<p>*******************************************/</p>\r\n\r\n<p>/*******************************************</p>\r\n\r\n<p>Adding Menus to Admin Panel and 02 Navigation menus to it</p>\r\n\r\n<p>*******************************************/</p>\r\n\r\n<p>if ( ! file_exists( get_template_directory() . &#39;/class-wp-bootstrap-navwalker.php&#39; ) ) {</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;// file does not exist... return an error.</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;return new WP_Error( &#39;class-wp-bootstrap-navwalker-missing&#39;, __( &#39;It appears the class-wp-bootstrap-navwalker.php file may be missing.&#39;, &#39;wp-bootstrap-navwalker&#39; ) );</p>\r\n\r\n<p>} else {</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;// file exists... require it.</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;require_once get_template_directory() . &#39;/class-wp-bootstrap-navwalker.php&#39;;</p>\r\n\r\n<p>}</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>function yoc_theme_setup(){</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;add_theme_support(&#39;menus&#39;);</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;register_nav_menu( &#39;primary&#39;, &#39;Primary Header Navigation&#39;);</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;register_nav_menu( &#39;secondary&#39;, &#39;Footer Navigation&#39;);</p>\r\n\r\n<p>}</p>\r\n\r\n<p>add_action(&#39;init&#39;,&#39;yoc_theme_setup&#39;);</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>/*******************************************</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Adding Theme Support Functions to Admin Panel</p>\r\n\r\n<p>*******************************************/</p>\r\n\r\n<p>add_theme_support(&#39;custom-background&#39;);</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>add_theme_support(&#39;custom-header&#39;);</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>add_theme_support(&#39;post-thumbnails&#39;);</p>\r\n\r\n<p>add_theme_support (&#39;post-formats&#39;, array(&#39;aside&#39;, &#39;image&#39;, &#39;video&#39;));</p>\r\n\r\n<p><br />\r\n&nbsp;</p>\r\n\r\n<p>/*******************************************</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Creating Sidebar Functions</p>\r\n\r\n<p>*******************************************/</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>function kaho_widget_setup(){</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;register_sidebar(</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;array(</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;name&#39;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;=&gt; &#39;Sidebar&#39;,</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;id&#39;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;=&gt; &#39;sidebar-1&#39;,</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;class&#39;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;=&gt;&nbsp;&nbsp;&#39;custom&#39;,</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;description&#39;&nbsp;&nbsp;&nbsp;=&gt;&nbsp;&nbsp;&#39;Standard Sidebar&#39;,</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;before_widget&#39; =&gt; &#39;</p>\r\n\r\n<ul>\r\n	<li>&#39;,\r\n	<p>&nbsp;</p>\r\n\r\n	<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;after_widget&#39; =&gt; &quot;</p>\r\n	</li>\r\n	<li>\\n&quot;,\r\n	<p>&nbsp;</p>\r\n\r\n	<p>&nbsp;&n