>}</p>\r\n\r\n	<p>&nbsp;</p>\r\n\r\n	<p>/*******************************************</p>\r\n\r\n	<p>Threaded comment reply set up</p>\r\n\r\n	<p>*******************************************/</p>\r\n\r\n	<p>&nbsp;</p>\r\n\r\n	<p>function xtreme_enqueue_comments_reply() {</p>\r\n\r\n	<p>&nbsp;&nbsp;&nbsp;&nbsp;if( get_option( &#39;thread_comments&#39; ) ) {</p>\r\n\r\n	<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;wp_enqueue_script( &#39;comment-reply&#39; );</p>\r\n\r\n	<p>&nbsp;&nbsp;&nbsp;&nbsp;}</p>\r\n\r\n	<p>}</p>\r\n\r\n	<p>add_action( &#39;comment_form_before&#39;, &#39;xtreme_enqueue_comments_reply&#39; );</p>\r\n\r\n	<p>&nbsp;</p>\r\n\r\n	<p>/* Function which displays your post date in time ago format */</p>\r\n\r\n	<p>function meks_time_ago() {</p>\r\n\r\n	<p>&nbsp;&nbsp;&nbsp;&nbsp;return human_time_diff( get_the_time( &#39;U&#39; ), current_time( &#39;timestamp&#39; ) ).&#39; &#39;.__( &#39;ago&#39; );</p>\r\n\r\n	<p>}</p>\r\n\r\n	<p>?&gt;</p>\r\n	</li>\r\n</ul>\r\n</blockquote>', 'mMHueRE9mcpSieUuq0Lm4Q1hgkD9j3B4osgYJiPA', '2019-08-06 08:40:41', '2019-08-06 08:45:18'),
(19, 'Wordpress', 'Header.php', '<blockquote>\r\n<p>&lt;!--&nbsp;&lt;!DOCTYPE&nbsp;html&gt;</p>\r\n\r\n<p>&lt;html&nbsp;&lt;?php&nbsp;//language_attributes();&nbsp;?&gt;&gt;</p>\r\n\r\n<p>&lt;head&gt;</p>\r\n\r\n<p>&lt;meta&nbsp;charset=&quot;&lt;?php&nbsp;//bloginfo(&nbsp;&#39;charset&#39;&nbsp;);&nbsp;?&gt;&quot;&gt;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p>&lt;meta&nbsp;charset=&quot;&lt;?php&nbsp;//bloginfo(&nbsp;&#39;charset&#39;&nbsp;);&nbsp;?&gt;&quot;&gt;</p>\r\n\r\n<p>&lt;title&gt;&lt;?php&nbsp;//wp_title(&#39;|&#39;,&nbsp;true,&nbsp;&#39;right&#39;);&nbsp;?&gt;&lt;/title&gt;</p>\r\n\r\n<p>&lt;meta&nbsp;name=&quot;viewport&quot;&nbsp;content=&quot;width=device-width,&nbsp;initial-scale=1&quot;&gt;</p>\r\n\r\n<p>&lt;link&nbsp;rel=&quot;pingback&quot;&nbsp;href=&quot;&lt;?php&nbsp;//bloginfo(&nbsp;&#39;pingback_url&#39;&nbsp;);&nbsp;?&gt;&quot;&gt;</p>\r\n\r\n<p>&lt;?php&nbsp;//wp_head();&nbsp;?&gt;</p>\r\n\r\n<p>&lt;/head&gt;</p>\r\n\r\n<p>&lt;body&gt;</p>\r\n\r\n<p>&lt;body&nbsp;class=&quot;home&nbsp;blog&quot;&gt;&nbsp;--&gt;</p>\r\n</blockquote>\r\n\r\n<p>&nbsp;</p>', 'mMHueRE9mcpSieUuq0Lm4Q1hgkD9j3B4osgYJiPA', '2019-08-06 08:48:30', '2019-09-14 04:17:47'),
