(18, 'Wordpress', 'Function.php', '<p><!--?php</p--></p>\r\n\r\n<blockquote>\r\n<p>&lt;?php</p>\r\n\r\n<p>/*******************************************</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Adding CSS and JS Files</p>\r\n\r\n<p>*******************************************/</p>\r\n\r\n<p>function kaho_style_enqueue() {</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;wp_enqueue_style(&#39;bootstrapcss&#39;,get_template_directory_uri().&#39;/css/bootstrap.css&#39;);</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;wp_enqueue_style(&#39;fontawesomecss&#39;,get_template_directory_uri().&#39;/css/font-awesome.css&#39;);</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;wp_enqueue_style(&#39;slickcss&#39;,get_template_directory_uri().&#39;/css/slick.css&#39;);</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;wp_enqueue_style(&#39;extracss&#39;,get_template_directory_uri().&#39;/css/extra.css&#39;);</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;wp_enqueue_style(&#39;stylecss&#39;,get_template_directory_uri().&#39;/css/style.css&#39;);</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;wp_enqueue_style( &#39;load-fa-1&#39;, &#39;//fonts.googleapis.com/css?family=EB+Garamond:400,400i,600,600i&#39; );</p>\r\n\r\n<p>}</p>\r\n\r\n<p>add_action( &#39;wp_enqueue_scripts&#39;, &#39;kaho_style_enqueue&#39; );</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>function kaho_script_enqueue() {</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;wp_enqueue_script(&#39;jqueryfile&#39;, get_template_directory_uri().&#39;/js/jquery.js&#39;, array(&#39;jquery&#39;), &#39;&#39;,true);</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;wp_enqueue_script(&#39;jqmigratejs&#39;, get_template_directory_uri().&#39;/js/jquery.migrate.js&#39;, array(&#39;jquery&#39;), &#39;&#39;,true);</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;wp_enqueue_script(&#39;slickjs&#39;, get_template_directory_uri().&#39;/js/slick.js&#39;, array(&#39;jquery&#39;), &#39;&#39;,true);</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;wp_enqueue_script(&#39;fitvidsjs&#39;, get_template_directory_uri().&#39;/js/fitvids.js&#39;, array(&#39;jquery&#39;), &#39;&#39;,true);</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;wp_enqueue_script(&#39;mainjs&#39;, get_template_directory_uri().&#39;/js/main.js&#39;, array(&#39;jquery&#39;), &#39;&#39;,true);</p>\r\n\r\n<p>}</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;add_action( &#39;wp_enqueue_scripts&#39;, &#39;kaho_script_enqueue&#39; );&nbsp;&nbsp;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p>/*******************************************</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Adding Menus to Admin Panel</p>\r\n\r\n<p>*******************************************/</p>\r\n\r\n<p>/*******************************************</p>\r\n\r\n<p>Adding Menus to Admin Panel and 02 Navigation menus to it</p>\r\n\r\n<p>*******************************************/</p>\r\n\r\n<p>if ( ! file_exists( get_template_directory() . &#39;/class-wp-bootstrap-navwalker.php&#39; ) ) {</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;// file does not exist... return an error.</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;return new WP_Error( &#39;class-wp-bootstrap-navwalker-missing&#39;, __( &#39;It appears the class-wp-bootstrap-navwalker.php file may be missing.&#39;, &#39;wp-bootstrap-navwalker&#39; ) );</p>\r\n\r\n<p>} else {</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;// file exists... require it.</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;require_once get_template_directory() . &#39;/class-wp-bootstrap-navwalker.php&#39;;</p>\r\n\r\n<p>}</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>function yoc_theme_setup(){</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;add_theme_support(&#39;menus&#39;);</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;register_nav_menu( &#39;primary&#39;, &#39;Primary Header Navigation&#39;);</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;register_nav_menu( &#39;secondary&#39;, &#39;Footer Navigation&#39;);</p>\r\n\r\n<p>}</p>\r\n\r\n<p>add_action(&#39;init&#39;,&#39;yoc_theme_setup&#39;);</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>/*******************************************</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Adding Theme Support Functions to Admin Panel</p>\r\n\r\n<p>*******************************************/</p>\r\n\r\n<p>add_theme_support(&#39;custom-background&#39;);</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>add_theme_support(&#39;custom-header&#39;);</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>add_theme_support(&#39;post-thumbnails&#39;);</p>\r\n\r\n<p>add_theme_support (&#39;post-formats&#39;, array(&#39;aside&#39;, &#39;image&#39;, &#39;video&#39;));</p>\r\n\r\n<p><br />\r\n&nbsp;</p>\r\n\r\n<p>/*******************************************</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Creating Sidebar Functions</p>\r\n\r\n<p>*******************************************/</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>function kaho_widget_setup(){</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;register_sidebar(</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;array(</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;name&#39;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;=&gt; &#39;Sidebar&#39;,</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;id&#39;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;=&gt; &#39;sidebar-1&#39;,</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;class&#39;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;=&gt;&nbsp;&nbsp;&#39;custom&#39;,</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;description&#39;&nbsp;&nbsp;&nbsp;=&gt;&nbsp;&nbsp;&#39;Standard Sidebar&#39;,</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;before_widget&#39; =&gt; &#39;</p>\r\n\r\n<ul>\r\n	<li>&#39;,\r\n	<p>&nbsp;</p>\r\n\r\n	<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;after_widget&#39; =&gt; &quot;</p>\r\n	</li>\r\n	<li>\\n&quot;,\r\n	<p>&nbsp;</p>\r\n\r\n	<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;before_title&#39; =&gt; &#39;</p>\r\n\r\n	<h2>&#39;,</h2>\r\n\r\n	<p>&nbsp;</p>\r\n\r\n	<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;after_title&#39; =&gt; &quot;</p>\r\n	\\n&quot;,\r\n\r\n	<p>&nbsp;</p>\r\n\r\n	<p>&nbsp;&nbsp;&nbsp;&nbsp;));</p>\r\n\r\n	<p>}</p>\r\n\r\n	<p>add_action(&#39;widgets_init&#39;, &#39;kaho_widget_setup&#39;);</p>\r\n\r\n	<p><br />\r\n	&nbsp;</p>\r\n\r\n	<p>/*******************************************</p>\r\n\r\n	<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Settiing Post Excerpts</p>\r\n\r\n	<p>*******************************************/</p>\r\n\r\n	<p>function custom_excerpt_length( $length ) {</p>\r\n\r\n	<p>&nbsp;&nbsp;&nbsp;&nbsp;return 10;</p>\r\n\r\n	<p>}</p>\r\n\r\n	<p>add_filter( &#39;excerpt_length&#39;, &#39;custom_excerpt_length&#39;, 999 );</p>\r\n\r\n	<p><br />\r\n	&nbsp;</p>\r\n\r\n	<p>/*******************************************</p>\r\n\r\n	<p>Settiing Global Avatars for users without Avatars</p>\r\n\r\n	<p>*******************************************/</p>\r\n\r\n	<p>&nbsp;</p>\r\n\r\n	<p>add_filter( &#39;avatar_defaults&#39;, &#39;new_default_avatar&#39; );</p>\r\n\r\n	<p>&nbsp;</p>\r\n\r\n	<p>function new_default_avatar ( $avatar_defaults ) {</p>\r\n\r\n	<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//Set the URL where the image file for your avatar is located</p>\r\n\r\n	<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$new_avatar_url = get_bloginfo( &#39;template_directory&#39; ) . &#39;/images/new_default_avatar.png&#39;;</p>\r\n\r\n	<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//Set the text that will appear to the right of your avatar in Settings&gt;&gt;Discussion</p>\r\n\r\n	<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$avatar_defaults[$new_avatar_url] = &#39;Your New Default Avatar&#39;;</p>\r\n\r\n	<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return $avatar_defaults;</p>\r\n\r\n	<p>}</p>\r\n\r\n	<p>&nbsp;</p>\r\n\r\n	<p>/*******************************************</p>\r\n\r\n	<p>Threaded comment reply set up</p>\r\n\r\n	<p>*******************************************/</p>\r\n\r\n	<p>&nbsp;</p>\r\n\r\n	<p>function xtreme_enqueue_comments_reply() {</p>\r\n\r\n	<p>&nbsp;&nbsp;&nbsp;&nbsp;if( get_option( &#39;thread_comments&#39; ) ) {</p>\r\n\r\n	<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;wp_enqueue_script( &#39;comment-reply&#39; );</p>\r\n\r\n	<p>&nbsp;&nbsp;&nbsp;&nbsp;}</p>\r\n\r\n	<p>}</p>\r\n\r\n	<p>add_action( &#39;comment_form_before&#39;, &#39;xtreme_enqueue_comments_reply&#39; );</p>\r\n\r\n	<p>&nbsp;</p>\r\n\r\n	<p>/* Function which displays your post date in time ago format */</p>\r\n\r\n	<p>function meks_time_ago() {</p>\r\n\r\n	<p>&nbsp;&nbsp;&nbsp;&nbsp;return human_time_diff( get_the_time( &#39;U&#39; ), current_time( &#39;timestamp&#39; ) ).&#39; &#39;.__( &#39;ago&#39; );</p>\r\n\r\n	<p>}</p>\r\n\r\n	<p>?&gt;</p>\r\n	</li>\r\n</ul>\r\n</blockquote>', 'mMHueRE9mcpSieUuq0Lm4Q1hgkD9j3B4osgYJiPA', '2019-08-06 08:40:41', '2019-08-06 08:45:18'),
(19, 'Wordpress', 'Header.php', '<blockquote>\r\n<p>&lt;!--&nbsp;&lt;!DOCTYPE&nbsp;html&gt;</p>\r\n\r\n<p>&lt;html&nbsp;&lt;?php&nbsp;//language_attributes();&nbsp;?&gt;&gt;</p>\r\n\r\n<p>&lt;head&gt;</p>\r\n\r\n<p>&lt;meta&nbsp;charset=&quot;&lt;?php&nbsp;//bloginfo(&nbsp;&#39;charset&#39;&nbsp;);&nbsp;?&gt;&quot;&gt;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p>&lt;meta&nbsp;charset=&quot;&lt;?php&nbsp;//bloginfo(&nbsp;&#39;charset&#39;&nbsp;);&nbsp;?&gt;&quot;&gt;</p>\r\n\r\n<p>&lt;title&gt;&lt;?php&nbsp;//wp_title(&#39;|&#39;,&nbsp;true,&nbsp;&#39;right&#39;);&nbsp;?&gt;&lt;/title&gt;</p>\r\n\r\n<p>&lt;meta&nbsp;name=&quot;viewport&quot;&nbsp;content=&quot;width=device-width,&nbsp;initial-scale=1&quot;&gt;</p>\r\n\r\n<p>&lt;link&nbsp;rel=&quot;pingback&quot;&nbsp;href=&quot;&lt;?php&nbsp;//bloginfo(&nbsp;&#39;pingback_url&#39;&nbsp;);&nbsp;?&gt;&quot;&gt;</p>\r\n\r\n<p>&lt;?php&nbsp;//wp_head();&nbsp;?&gt;</p>\r\n\r\n<p>&lt;/head&gt;</p>\r\n\r\n<p>&lt;body&gt;</p>\r\n\r\n<p>&lt;body&nbsp;class=&quot;home&nbsp;blog&quot;&gt;&nbsp;--&gt;</p>\r\n</blockquote>\r\n\r\n<p>&nbsp;</p>', 'mMHueRE9mcpSieUuq0Lm4Q1hgkD9j3B4osgYJiPA', '2019-08-06 08:48:30', '2019-09-14 04:17:47'),
(20, 'Wordpress', 'Footer.php', '<blockquote>\r\n<p>&lt;?php wp_footer(); ?&gt;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&lt;/body&gt;</p>\r\n\r\n<p>&lt;/html&gt;</p>\r\n</blockquote>', 'mMHueRE9mcpSieUuq0Lm4Q1hgkD9j3B4osgYJiPA', '2019-08-06 08:49:13', '2019-08-06 08:49:13'),
(21, 'Wordpress', 'Handling Images in Wordpress', '<p>Use</p>\r\n\r\n<blockquote>\r\n<p>img src=&quot;echo get_template_directory_uri(); ?&gt;/images/b1.jpg<!--?php echo get_template_directory_uri(); ?-->/images/b1.jpg&quot; alt=&quot; &quot;</p>\r\n</blockquote>\r\n\r\n<p>Use this instead of thumbnail</p>\r\n\r\n<p>To call the url and alt</p>\r\n\r\n<blockquote>\r\n<p>$url = wp_get_attachment_url(get_post_thumbnail_id($post-&gt;ID));</p>\r\n\r\n<p>$alt = get_post_meta(get_post_thumbnail_id($post-&gt;ID), &#39;_wp_attachment_image_alt&#39;, true); &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p>$title = get_post(get_post_thumbnail_id())-&gt;post_title;</p>\r\n\r\n<p>&nbsp;</p>\r\n</blockquote>\r\n\r\n<p>Use this in html</p>\r\n\r\n<blockquote>\r\n<p>img src=&quot;<!--?php echo $url; ?-->&quot; alt=&quot;<!--?php echo $alt; ?-->&quot; title=&quot;<!--?php echo $title; ?-->&quot; class=&quot;img-responsive&quot; itemprop=&quot;image&quot;</p>\r\n</blockquote>\r\n\r\n<p>To use as Background</p>\r\n\r\n<blockquote>\r\n<p>style=&quot;background-image:url(&#39;<!--?php echo $url ?-->&#39;); background-size: cover;&quot;&gt;</p>\r\n</blockquote>\r\n\r\n<p>Bringing blogs to home page, we need to make sure the images are of low size. use this</p>\r\n\r\n<blockquote>\r\n<p><!--?php the_post_thumbnail( \'medium\', [\'class\' =-->&#39;img-responsive responsive--full&#39;]); ?&gt;</p>\r\n</blockquote>\r\n\r\n<p>Change the size of the thumbanil is admin panel Setting&gt; Media</p>', 'mMHueRE9mcpSieUuq0Lm4Q1hgkD9j3B4osgYJiPA', '2019-08-06 09:17:05', NULL),
(22, 'Wordpress', 'Loop in wordpress', '<p>Try any of these loops</p>\r\n\r\n<p>1.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; This has &ldquo;<strong>URL</strong>&rdquo; for featured image to be shown as background image through inline CSS.</p>\r\n\r\n<blockquote>\r\n<p>&lt;?php &nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$args =array(</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;type&#39;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;=&gt; &#39;posts&#39;,</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;posts_per_page&#39;&nbsp;&nbsp;&nbsp;&nbsp;=&gt; 1,</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;);</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$lastblog = new WP_QUERY($args);</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if ($lastblog-&gt;have_posts()) {</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;while ($lastblog-&gt;have_posts()) {</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$lastblog-&gt;the_post();</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$url = wp_get_attachment_url(get_post_thumbnail_id($post-&gt;ID));</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;?&gt;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;div class=&quot;item active&quot; style=&quot;background-image:url(&#39;&lt;?php echo $url ?&gt;&#39;); background-size: cover;&quot;&gt;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;div class=&quot;container&quot;&gt;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;div class=&quot;carousel-caption&quot;&gt;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;div class=&quot;col-md-12 slider_left&quot;&gt;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;h2&gt;&lt;?php the_title(); ?&gt;&lt;/h2&gt;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;p&gt;&lt;?php the_excerpt(); ?&gt;&lt;/p&gt;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;a href=&quot;&lt;?php the_permalink(); ?&gt;&quot; class=&quot;hvr-bounce-to-right read scroll&quot;&gt;&lt;span class=&quot;fa fa-birthday-cake&quot; aria-hidden=&quot;true&quot;&gt;&lt;/span&gt;Read More&lt;/a&gt;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;/div&gt;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;div class=&quot;clearfix&quot;&gt;&lt;/div&gt;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;/div&gt;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;/div&gt;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;/div&gt;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;?php</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;wp_reset_postdata();</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;?&gt;</p>\r\n</blockquote>', 'mMHueRE9mcpSieUuq0Lm4Q1hgkD9j3B4osgYJiPA', '2019-08-06 09:17:56', '2019-08-06 09:17:56'),
(23, 'Wordpress', 'Category Lists', '<blockquote>\r\n<p>&lt;ul&gt;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;?php</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $categories = get_categories( array(</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &#39;orderby&#39; =&gt; &#39;name&#39;,</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &#39;order&#39;&nbsp;&nbsp; =&gt; &#39;ASC&#39;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ) );?&gt;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;?php</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; foreach( $categories as $category ) {</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $category_link = sprintf(</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &#39;&lt;a href=&quot;%1$s&quot; alt=&quot;%2$s&quot;&gt;%3$s&lt;/a&gt;&#39;,</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; esc_url( get_category_link( $category-&gt;term_id ) ),</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; esc_attr( sprintf( __( &#39;View all posts in %s&#39;, &#39;textdomain&#39; ), $category-&gt;name ) ),</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; esc_html( $category-&gt;name )</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; );</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; echo &#39;&lt;li&gt;&lt;i class=&quot;fa fa-check&quot; aria-hidden=&quot;true&quot;&gt;&lt;/i&gt;&#39; . sprintf( esc_html__( &#39; %s&#39;, &#39;textdomain&#39; ), $category_link ) . &#39; &lt;/li&gt; &#39;;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ?&gt;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;/ul&gt;</p>\r\n</blockquote>', 'mMHueRE9mcpSieUuq0Lm4Q1hgkD9j3B4osgYJiPA', '2019-08-06 09:18:44', '2019-08-06 09:18:44'),
(24, 'Wordpress', 'Tag Lists', '<p><strong>Option 1:</strong></p>\r\n\r\n<blockquote>\r\n<p>&lt;?php</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $tags = get_tags(array(</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &#39;hide_empty&#39; =&gt; false</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ));</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; echo &#39;&lt;ul&gt;&#39;;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; foreach ($tags as $tag) {</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; echo &#39;&lt;li&gt;&lt;a href=&quot;&#39;. get_term_link($tag).&#39;&quot;&gt;&#39; . $tag-&gt;name . &#39;&lt;/li&gt;&#39;;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; echo &#39;&lt;/ul&gt;&#39;;</p>\r\n\r\n<p>&nbsp;?&gt;</p>\r\n</blockquote>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Option 2:</strong></p>\r\n\r\n<blockquote>\r\n<p>&lt;div class=&quot;twt&quot;&gt;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;?php</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; wp_tag_cloud( array(</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &#39;smallest&#39; =&gt; 12, // size of least used tag</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &#39;largest&#39;&nbsp; =&gt; 30, // size of most used tag</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &#39;unit&#39;&nbsp;&nbsp;&nbsp;&nbsp; =&gt; &#39;px&#39;, // unit for sizing the tags</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &#39;number&#39;&nbsp;&nbsp; =&gt; 30, // displays at most 45 tags</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &#39;orderby&#39;&nbsp; =&gt; &#39;name&#39;, // order tags alphabetically</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &#39;order&#39;&nbsp;&nbsp;&nbsp; =&gt; &#39;ASC&#39;, // order tags by ascending order</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &#39;taxonomy&#39; =&gt; &#39;post_tag&#39; // you can even make tags for custom taxonomies</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ) );</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; ?&gt;</p>\r\n\r\n<p>&lt;/div&gt;</p>\r\n</blockquote>', 'mMHueRE9mcpSieUuq0Lm4Q1hgkD9j3B4osgYJiPA', '2019-08-06 09:20:10', '2019-08-06 09:20:10'),
(25, 'Wordpress', 'To bring title and description file dynamically from admin panel', '<p>Meta for pages.</p>\r\n\r\n<p>Include the below code in header file for title</p>\r\n\r\n<blockquote>\r\n<p>&lt;title&gt;&lt;?php wp_title(&#39;|&#39;, true, &#39;right&#39;); ?&gt;&lt;/title&gt;</p>\r\n</blockquote>\r\n\r\n<p>Add below in headers.php</p>\r\n\r\n<blockquote>\r\n<p>&lt;title&gt;&lt;?php bloginfo(&#39;name&#39;); ?&gt;&lt;/title&gt;</p>\r\n\r\n<p>&lt;meta name=&quot;description&quot; content=&quot;&lt;?php bloginfo(&#39;description&#39;) ?&gt;&quot;/&gt;</p>\r\n</blockquote>\r\n\r\n<p>You can also add below in functions file but it will put title and description together as one meta</p>\r\n\r\n<p>// To bring title dynamically from admin panel</p>\r\n\r\n<blockquote>\r\n<p>add_theme_support( &#39;title-tag&#39; );</p>\r\n</blockquote>\r\n\r\n<p>Also add in headers</p>\r\n\r\n<blockquote>\r\n<p>&lt;meta charset=&quot;&lt;?php bloginfo(&#39;charset&#39;); ?&gt;&quot;&gt;</p>\r\n</blockquote>', 'mMHueRE9mcpSieUuq0Lm4Q1hgkD9j3B4osgYJiPA', '2019-08-06 09:22:42', '2019-08-06 09:22:42'),
(26, 'Wordpress', 'Page- Template', '<blockquote>\r\n<p>&lt;?php /* Template Name: page-template */ ?&gt;</p>\r\n\r\n<p>&lt;?php</p>\r\n\r\n<p>if(have_posts()):</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;while (have_posts()) :</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;the_post();</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;the_content();</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;endwhile;</p>\r\n\r\n<p>endif;</p>\r\n\r\n<p>?&gt;</p>\r\n\r\n<p>&lt;?php get_footer(); ?&gt;</p>\r\n</blockquote>', 'pNeLKsEh8WhRyhNjqUdUEZ0wlhGiWAmiyr3TwUDB', '2019-08-13 05:47:08', '2019-08-13 05:47:08'),
(27, 'Wordpress', 'Useful Codes', '<p>To get directory path</p>\r\n\r\n<blockquote>\r\n<p>echo get_template_directory_uri(); ?&gt;/images/b1.jpg</p>\r\n</blockquote>', 'cHKhnGaoYTHBdMrLL38lxzm80F47YpyCu4sdIUV9', '2019-08-14 10:40:23', NULL),
(28, 'Frontend', 'Parallax', '<p>For Parallax, put in&nbsp;CSS</p>\r\n\r\n<blockquote>\r\n<p>.parallax&nbsp;{&nbsp;<br />\r\n&nbsp;&nbsp;background:&nbsp;linear-gradient(#243b551a,#141e30a6),url(../images/b3.jpg) no-repeat center<br />\r\n&nbsp;&nbsp;height:&nbsp;500px;&nbsp;<br />\r\n&nbsp; background-attachment:&nbsp;fixed;<br />\r\n&nbsp; background-position:&nbsp;center;<br />\r\n&nbsp; background-repeat:&nbsp;no-repeat;<br />\r\n&nbsp;&nbsp;background-size:&nbsp;cover;<br />\r\n}</p>\r\n</blockquote>', '3tXFPem8i82ov87gbWLLKmBURvHhL6gyMfio2eMC', '2019-08-17 09:52:07', NULL),
(29, 'Wordpress', 'Woo COmmerce Pages in Admin Panel', '<p>When you install woo commerce, relevant pages will be created in admin panel</p>\r\n\r\n<p>In cart page and put his in admin panel</p>\r\n\r\n<blockquote>\r\n<p><strong>[woocommerce_cart]</strong></p>\r\n</blockquote>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Create a Check out page and put this in admin panel</p>\r\n\r\n<blockquote>\r\n<p><strong>[woocommerce_checkout]</strong></p>\r\n</blockquote>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>', 'Ispkmoh5RICpn2DN2fE6W9AiMFmMa5rG5BLQES1d', '2019-08-22 03:24:30', NULL),
(30, 'Wordpress', 'Contact form - Flamingo', '<p>General form html</p>\r\n\r\n<blockquote>\r\n<p>[text* text-582 class:form-control placeholder &quot;Name&quot;]</p>\r\n</blockquote>\r\n\r\n<p>Install Contact form 7 and flamingo</p>\r\n\r\n<p>To Include the contact form in footer use this code</p>\r\n\r\n<blockquote>\r\n<p>echo do_shortcode( &#39;[contact-form-7 id=&quot;826&quot; title=&quot;Contact Form&quot;]&#39; );</p>\r\n</blockquote>\r\n\r\n<p><strong>Adjust the rows and columns of textarea</strong></p>\r\n\r\n<p>Simply put 10x2</p>\r\n\r\n<blockquote>\r\n<p>[textarea your-message 10x2 class:form-input placeholder &quot;MESSAGE&quot;]</p>\r\n</blockquote>\r\n\r\n<p>The above will generate a textarea with cols=&quot;10&quot; and rows=&quot;2&quot;</p>\r\n\r\n<p>Put this in css</p>\r\n\r\n<blockquote>\r\n<p>div.wpcf7-response-output, div.wpcf7-mail-sent-ok {</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp; color: #fff;</p>\r\n\r\n<p>}</p>\r\n</blockquote>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Go to do settings&gt;Discussions and add this in comment blacklist</p>\r\n\r\n<p>sex</p>\r\n\r\n<p>girl</p>\r\n\r\n<p>porn</p>\r\n\r\n<p>nude</p>\r\n\r\n<p>horny</p>\r\n\r\n<p>bitch</p>\r\n\r\n<p>Viagra</p>\r\n\r\n<p>Gambling</p>\r\n\r\n<p>Cryptocurrencies</p>\r\n\r\n<p>Cryptocurrency</p>\r\n\r\n<p>$</p>\r\n\r\n<p>Bitcoin</p>\r\n\r\n<p>USD</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>', 'zlBakLN80ZfIV5XmtV5BO5wlL5tIQMx0sdVhZ1yb', '2019-08-22 07:42:46', NULL),
(31, 'Wordpress', 'Essential Pages', '<p><strong>Search Page</strong></p>\r\n\r\n<p>To display the search text use</p>\r\n\r\n<blockquote>\r\n<p>&lt;?php printf( esc_html__( &#39;%s&#39;, &#39;noteblog&#39; ), &#39;&lt;h2 class=&quot;title1&quot;&gt;You searched for : &#39; . get_search_query() . &#39;&lt;/h2&gt;&#39; ); ?&gt;</p>\r\n</blockquote>\r\n\r\n<p>To display the search result use:</p>\r\n\r\n<blockquote>\r\n<p>$args =array(</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;type&#39;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; =&gt; &#39;posts&#39;,</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;posts_per_page&#39;&nbsp;&nbsp;&nbsp; =&gt; -1,</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;s&#39; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;=&gt; $_GET[&#39;s&#39;]</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;);</p>\r\n</blockquote>\r\n\r\n<p><strong>Category Page</strong></p>\r\n\r\n<p>Create Category.php</p>\r\n\r\n<p>To display the search text use</p>\r\n\r\n<blockquote>\r\n<p>&lt;?php printf( esc_html__( &#39;%s&#39;, &#39;noteblog&#39; ), &#39;&lt;h2 class=&quot;title1&quot;&gt;Blogs of category: &#39; . single_cat_title( &#39;&#39;, false ) . &#39;&lt;/h2&gt;&#39; ); ?&gt;</p>\r\n</blockquote>\r\n\r\n<p>To display the search result use:</p>\r\n\r\n<blockquote>\r\n<p>$args =array(</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;category_name&#39;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;=&gt;&nbsp; get_cat_name(get_query_var(&#39;cat&#39;), false),</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;type&#39;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; =&gt; &#39;posts&#39;,</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;posts_per_page&#39;&nbsp;&nbsp;&nbsp; =&gt; 10,</p>\r\n\r\n<p>);</p>\r\n</blockquote>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>You can test the same with</p>\r\n\r\n<blockquote>\r\n<p>$cat_id = get_cat_name(get_query_var(&#39;cat&#39;), false); ?&gt;</p>\r\n\r\n<p>&lt;p&gt;The selected category is: &lt;?php echo $cat_id ?&gt;&lt;/p&gt;</p>\r\n</blockquote>\r\n\r\n<p><strong>Tag Page:</strong></p>\r\n\r\n<p>Use this loop</p>\r\n\r\n<blockquote>\r\n<p>&lt;?php</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;the_archive_title( &#39;&lt;h3 class=&quot;heading&quot;&gt;&#39;, &#39;&lt;/h3&gt;&#39; ); ?&gt;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;?php</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$tag = get_queried_object();</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$args =array(</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;type&#39;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;=&gt; &#39;posts&#39;,</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;posts_per_page&#39;&nbsp;&nbsp;&nbsp;&nbsp;=&gt; -1,</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;tag&#39;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;=&gt; $tag-&gt;slug</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;);</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$lastblog = new WP_QUERY($args);</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if ($lastblog-&gt;have_posts()) {</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;while ($lastblog-&gt;have_posts()) {</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$lastblog-&gt;the_post();</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$url = wp_get_attachment_url(get_post_thumbnail_id($post-&gt;ID));</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;?&gt;</p>\r\n</blockquote>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Thank you page redirection</p>\r\n\r\n<p>Add the below to functions.php</p>\r\n\r\n<blockquote>\r\n<p>add_action( &#39;wp_footer&#39;, &#39;mycustom_wp_footer&#39; );</p>\r\n\r\n<p>function mycustom_wp_footer() {</p>\r\n\r\n<p>?&gt;</p>\r\n\r\n<p>&lt;script&gt;</p>\r\n\r\n<p>document.addEventListener( &#39;wpcf7mailsent&#39;, function( event ) {</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp; location = &#39;https://www.nrskitchen.com/thankyou&#39;;</p>\r\n\r\n<p>}, false );</p>\r\n\r\n<p>&lt;/script&gt;</p>\r\n\r\n<p>&lt;?php</p>\r\n\r\n<p>}</p>\r\n</blockquote>', 'Ty2c8NdVoZQc2E7PdSeATkbQYoxLm9gPcX1DS88a', '2019-08-22 10:56:51', '2019-08-22 10:56:51'),
(32, 'Wordpress', 'Add whats app link', '<p>Put this in Footer</p>\r\n\r\n<blockquote>\r\n<p>&lt;script type=&quot;text/javascript&quot;&gt;</p>\r\n\r\n<p>(function () {</p>\r\n\r\n<p>var options = {</p>\r\n\r\n<p>whatsapp: &quot;+91-8527505252&quot;, // WhatsApp number</p>\r\n\r\n<p>call_to_action: &quot;Say hi&quot;, // Call to action</p>\r\n\r\n<p>position: &quot;right&quot;, // Position may be &#39;right&#39; or &#39;left&#39;</p>\r\n\r\n<p>};</p>\r\n\r\n<p>var proto = document.location.protocol, host = &quot;whatshelp.io&quot;, url = proto + &quot;//static.&quot; + host;</p>\r\n\r\n<p>var s = document.createElement(&#39;script&#39;); s.type = &#39;text/javascript&#39;; s.async = true; s.src = url + &#39;/widget-send-button/js/init.js&#39;;</p>\r\n\r\n<p>s.onload = function () { WhWidgetSendButton.init(host, proto, options); };</p>\r\n\r\n<p>var x = document.getElementsByTagName(&#39;script&#39;)[0]; x.parentNode.insertBefore(s, x);</p>\r\n\r\n<p>})();</p>\r\n\r\n<p>&lt;/script&gt;</p>\r\n</blockquote>', 'TEZ2ozi6vgyG6erq2Q49uFpdZI73XBno6FcXH3Jr', '2019-09-04 10:24:56', '2019-09-04 10:24:56'),
(33, 'Frontend', 'Breadcrumb', '<p>For HTML</p>\r\n\r\n<blockquote>\r\n<p>&lt;nav&nbsp;class=&quot;breadcrumb&quot;&gt;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&lt;ul&nbsp;class=&quot;breadcrumb&quot;&gt;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;li&gt;&lt;a&nbsp;href=&quot;/&quot;&gt;Home&lt;/a&gt;&lt;/li&gt;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;li&gt;&lt;a&nbsp;href=&quot;#&quot;&gt;Home&lt;/a&gt;&lt;/li&gt;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;li&gt;&lt;a&nbsp;href=&quot;#&quot;&gt;Home&lt;/a&gt;&lt;/li&gt;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;li&gt;&lt;a&nbsp;href=&quot;#&quot;&gt;Home&lt;/a&gt;&lt;/li&gt;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;li&gt;&lt;a&nbsp;href=&quot;#&quot;&gt;Home&lt;/a&gt;&lt;/li&gt;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;li&gt;&lt;a&nbsp;href=&quot;#&quot;&gt;Home&lt;/a&gt;&lt;/li&gt;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&lt;/ul&gt;</p>\r\n\r\n<p>&lt;/nav&gt;</p>\r\n</blockquote>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>For style</p>\r\n\r\n<blockquote>\r\n<p>.breadcrumb{</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;margin:&nbsp;0;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;background-color:&nbsp;#c1c9c8;</p>\r\n\r\n<p>}</p>\r\n\r\n<p>.breadcrumb&nbsp;li{</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;padding:&nbsp;0&nbsp;15px;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;border-right:&nbsp;2px&nbsp;solid&nbsp;#000;</p>\r\n\r\n<p>}</p>\r\n\r\n<p>.breadcrumb&nbsp;li:last-child{</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;border:&nbsp;none;</p>\r\n\r\n<p>}</p>\r\n\r\n<p>.breadcrumb&nbsp;li&nbsp;a{</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;color:&nbsp;#000;</p>\r\n\r\n<p>}</p>\r\n</blockquote>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>', 'saGNbj8alJfL3almtHpqzAAPb9yRpWtQXnKBShgs', '2019-09-09 03:38:10', '2019-09-09 03:38:10'),
(34, 'Wordpress', '404.php', '<p>Simply create a file in the them &ndash; 404.php and put below code in it.</p>\r\n\r\n<blockquote>\r\n<p>&lt;?php</p>\r\n\r\n<p>header(&quot;HTTP/1.1 301 Moved Permanently&quot;);</p>\r\n\r\n<p>header(&quot;Location: &quot;.get_bloginfo(&#39;url&#39;));</p>\r\n\r\n<p>exit();</p>\r\n\r\n<p>?&gt;</p>\r\n</blockquote>', 'tIJDIMu1D2QDoAB5rLyVf2DVxn4NeuAPIJrqppcD', '2019-09-14 04:10:43', '2019-09-14 04:10:43'),
(35, 'Wordpress', 'Dates in Wordpress', '<p>In functions file</p>\r\n\r\n<blockquote>\r\n<p>/* Function which displays your post date in time ago format */</p>\r\n\r\n<p>function meks_time_ago() {</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;return human_time_diff( get_the_time( &#39;U&#39; ), current_time( &#39;timestamp&#39; ) ).&#39; &#39;.__( &#39;ago&#39; );</p>\r\n\r\n<p>}</p>\r\n</blockquote>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>In page file</p>\r\n\r\n<blockquote>\r\n<p>&lt;?php echo meks_time_ago(); /* post date in time ago format */ ?&gt;</p>\r\n</blockquote>', 'tIJDIMu1D2QDoAB5rLyVf2DVxn4NeuAPIJrqppcD', '2019-09-14 04:19:28', '2019-09-14 04:19:28'),
